#return the min integer you 
#can make after k digit swaps
def minInteger(num,k):
  ret = ""
  while k > 0:
    reach = min(k,len(num)-1)
    interval = num[:reach+1]
    index = min_and_index(interval)
    if index >= 0:
      ret += num[index]
      num = num[:index] + num[index+1:]
      k -= index
    #Check if totally sorted
    else:
      count = check_sorted(num)
      if count < 0:
        k = 0
      else:
        ret += num[:count]
        num = num[count:]

  return ret + num

def min_and_index(S):
  val, index = 10, 0
  stop_count = 0
  for i in range(len(S)-1,-1,-1):
    if int(S[i]) <= val:
      val = int(S[i])
      index = i
      stop_count += 1

  if stop_count == len(S):
    return -1
  
  return index


def check_sorted(S):
  count = 0
  for i in range(1,len(S)):
    if int(S[i]) < int(S[i-1]):
      return count
    count += 1
  return -1


